package March2022;

public class EmployeePayCheck {

	
		// TODO Auto-generated method stub

		static int AdditionPayWeekly(int PayWeek1,int PayWeek2) {
			return PayWeek1+PayWeek2;
	}
		static int MultiplicatePayWeekly(int PayWeek1,int PayWeek2) {
			return PayWeek1*PayWeek2;
		}
		static int SubtractPayWeekly(int PayWeek1,int PayWeek2) {
			return PayWeek1-PayWeek2;
		}
		static int DivisionPayweekly(int PayWeek1,int PayWeek2) {
			return PayWeek1/PayWeek2;
		}
}